#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\kotow\projects\articles\vue-modal-trap-focus\node_modules\.pnpm\rollup@2.58.0\node_modules\rollup\dist\bin\node_modules;C:\Users\kotow\projects\articles\vue-modal-trap-focus\node_modules\.pnpm\rollup@2.58.0\node_modules\rollup\dist\node_modules;C:\Users\kotow\projects\articles\vue-modal-trap-focus\node_modules\.pnpm\rollup@2.58.0\node_modules\rollup\node_modules;C:\Users\kotow\projects\articles\vue-modal-trap-focus\node_modules\.pnpm\rollup@2.58.0\node_modules;C:\Users\kotow\projects\articles\vue-modal-trap-focus\node_modules\.pnpm\node_modules;C:\Users\kotow\projects\articles\vue-modal-trap-focus\node_modules;C:\Users\kotow\projects\articles\node_modules;C:\Users\kotow\projects\node_modules;C:\Users\kotow\node_modules;C:\Users\node_modules;C:\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/kotow/projects/articles/vue-modal-trap-focus/node_modules/.pnpm/rollup@2.58.0/node_modules/rollup/dist/bin/node_modules:/mnt/c/Users/kotow/projects/articles/vue-modal-trap-focus/node_modules/.pnpm/rollup@2.58.0/node_modules/rollup/dist/node_modules:/mnt/c/Users/kotow/projects/articles/vue-modal-trap-focus/node_modules/.pnpm/rollup@2.58.0/node_modules/rollup/node_modules:/mnt/c/Users/kotow/projects/articles/vue-modal-trap-focus/node_modules/.pnpm/rollup@2.58.0/node_modules:/mnt/c/Users/kotow/projects/articles/vue-modal-trap-focus/node_modules/.pnpm/node_modules:/mnt/c/Users/kotow/projects/articles/vue-modal-trap-focus/node_modules:/mnt/c/Users/kotow/projects/articles/node_modules:/mnt/c/Users/kotow/projects/node_modules:/mnt/c/Users/kotow/node_modules:/mnt/c/Users/node_modules:/mnt/c/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../dist/bin/rollup" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../dist/bin/rollup" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../dist/bin/rollup" $args
  } else {
    & "node$exe"  "$basedir/../../dist/bin/rollup" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
